	//enable
	var activeFields = function(o){
		var carga = $('[name=carga]'),
			toneladas = $('[name=toneladas]'),
			motivo = $('[name=motivo]');
		if(o == "C"){				
			carga.attr('disabled', false);
			toneladas.attr('disabled', false);	
			motivo.attr('disabled', true);
			savePds(motivo,{value:0,label:"No aplica"});
		}else if(o=="B"){
			motivo.attr('disabled', true);
			carga.attr('disabled', true);
			toneladas.attr('disabled', true);

			savePds(motivo,{value:0,label:"No aplica"});
			savePds(carga,{value:0,label:"No aplica"});
			savePds(toneladas,{value:0,label:"No aplica"});	
		}else{
			carga.attr('disabled', true);
			toneladas.attr('disabled', true);
			motivo.attr('disabled', false);

			savePds(carga,{value:0,label:"No aplica"});
			savePds(toneladas,{value:0,label:"No aplica"});
		}

		carga.removeClass('has-error');
		toneladas.removeClass('has-error');
		motivo.removeClass('has-error');	
	}	

	function validate(){		
		var all = $('input.form-control:required'),
			status = true;		
		$.each(all, function(index, f) {
			var field = $(f);
			if(!field.attr('disabled')){				
				if(saveData[field.attr('name')] == undefined) {					
					field.addClass('has-error');
					status = false;					
				}else{
					field.removeClass('has-error');	
				}	
			}			
		});

		if(!status){
			alert("Campos oblicatorios ó los datos no son validos");
			return false;
		}

		return true;		
	}

	var logs = {
		panel : $('.logs').find('code'),
		text : "",
		btnClear : $('.logs').find('button'),
		init : function(){					
			this.event();
			this.start();
		},
		event : function(){
			var _this = this;
			this.btnClear.click(function(event) {
				_this.clear();
			});
		},
		start : function(){	
			var currentdate = new Date(),
				time = currentdate.getHours() + ":"+ currentdate.getMinutes();

			this.add("Inicio de captura: "+time);
			this.add("Capturista: "+saveData.capturista);
		},
		add : function(t){
			this.panel.html(t+"<br>"+this.text);
			this.text = this.panel.html();	
		},
		link : function(data){
			var date = new Date(data.fechaCaptura.sec*1000),				          
                hours = date.getHours(),
                minutes = date.getMinutes(),
                seconds = date.getSeconds(),
                d = 'encuesta - '+hours+':'+minutes+':'+seconds;

			this.add('<a target="_blank" href="edit.php?id='+data._id.$id+'">'+d+'</a>');	
		},
		clear : function(){
			this.panel.html('');
			this.text = "";
		}
	};

